mapper.select:
	
		SELECT ifnull(RM, 0)
		FROM(
	
    		SELECT MAX(@RNUM:=@RNUM+1) AS RM
    		
    		FROM (select * from magazine) AS T1, (SELECT @RNUM:=0) AS R 
			)  AS T2 
  		ORDER BY RM DESC;

SELECT magKey, magTitle, magSubTitle, magContent, date_format(magDate, '%b/%e, %Y')AS magDate, magHit, magImg
FROM magazine
WHERE MAGKEY = #{magkey}

mapper.update:
UPDATE magazine
SET magHit = magHit + 1
WHERE MAGKEY = #{magkey}
